const styles = {
	black: [30, 40],
	red: [31, 41],
	green: [32, 42],
	yellow: [33, 43],
	blue: [34, 44],
	magenta: [35, 45],
	cyan: [36, 46],
	white: [37, 47],
	normal: 0,
	bold: 1,
	underline: 4,
	blinking: 5,
	reverse: 7
}

function toANSI (style, isBG) {
	style = styles[style];

	if (typeof style !== 'undefined') {
		if (typeof style === 'object') {
			if (isBG) {
				return style[1];
			}

			return style[0];
		}

		return style;
	} 

	return false;	
}

module.exports = function (color, str) {
	color = color.toLowerCase();

	if (typeof color !== 'undefined') {
		if (color.indexOf('bg') !== -1) {
			color = color.replace('bg', '');

			return '\u001B[' +  toANSI(color, true) +  'm' + str + '\u001B[39m';
		} else {
			return '\u001B[' +  toANSI(color, false) + 'm' + str + '\u001B[39m';
		}
	}
}